import { loadEnv } from "vite";
const processEnvVariables = (prefix = "VITE") => {
  const mode = process.env.NODE_ENV || "development";
  let processEnv = loadEnv(mode, process.cwd(), "");
  processEnv = Object.fromEntries(
    Object.entries(processEnv).filter(([env]) => env.startsWith(prefix)).map(([key, value]) => [key, JSON.stringify(value)])
  );
  return processEnv;
};
export {
  processEnvVariables
};
